#include <stdio.h>
#include <string.h>
#include <stdbool.h>
#include <stdlib.h>

struct Node {
    int key;
    int data;
    struct Node* next;
};
struct Node* head = NULL;
struct Node* current = NULL;

void insertFirst(int key, int data) {
    struct Node* temp = (struct Node*)malloc(sizeof(struct Node));
    temp->key = key;
    temp->data = data;
    temp->next = head;
    head = temp;
}
void insertAfter(struct Node* after, int key, int data) {
    struct Node* new_node = (struct Node*)malloc(sizeof(struct Node));
    new_node->data = data;
    new_node->key = key;
    new_node->next = after->next;
    after->next = new_node;
}
void printList() {
    struct Node* printl = head;
    printf("Linked list :- ");
    while (printl != NULL) {
        printf("[%d, ",printl->key);
        printf("%d]",printl->data);
        if (printl->next != NULL)
            printf(",");
        printl = printl->next;
    }
}

bool isEmpty() {
    return head == NULL;
}
